import axios from 'axios';

interface NewsArticle {
    source: {
        id: string | null;
        name: string;
    };
    author: string | null;
    title: string;
    description: string;
    url: string;
    urlToImage: string | null;
    publishedAt: string;
    content: string | null;
}

interface NewsApiResponse {
    status: string;
    totalResults: number;
    articles: NewsArticle[];
}


const API_KEY: string = "a57be76ec5a644e6863fff5953e3dbdd";
const BASE_URL: string = 'https://newsapi.org/v2/';

export const fetchNews = async (category: string): Promise<NewsArticle[]> => {
    try {
        const response = await axios.get<NewsApiResponse>(`${BASE_URL}top-headlines`, {
            params: {
                category: category,
                apiKey: API_KEY,
                // Additional parameters can be added here
            },
        });

        return response.data;
    } catch (error) {
        console.error("Error fetching news", error);
        throw error; // Rethrow the error for handling in the component
    }
};
